INSERT INTO places VALUES ('poi-039', 'Jardin du Luxembourg', 'jardin-du-luxembourg', 'parc', NULL, 'chill', 48.8466144, 2.3363309, 6, '75006 Paris', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, NULL, NULL, NULL, NULL, NULL, NULL, '{"chill":{"overall":24,"criteria":{}},"festif":{"overall":2,"criteria":{}},"culturel":{"overall":2,"criteria":{}}}', NULL, '["parc"]', NULL, '{"primary_status":null,"tags":["pelouse_interdite"],"main_action":{"type":"site","url":"https://jardin.senat.fr/","label":"Site du lieu"},"accessibility":true,"wifi_available":false,"opening_hours":"Tlj: 08h-20h","price_range":1,"smart_tip":"Un lieu incontournable du 6e arrondissement."}', '{"budget_avg":0,"is_free":true}', NULL, NULL, 'Le ''Luco'' pour les intimes. C''est le jardin du Sénat, mais c''est surtout le jardin des Parisiens. Il a deux visages : le côté grand style à la française autour du bassin central où les enfants poussent des voiliers en bois (une tradition depuis 1920), et le côté anglais plus sauvage et romantique vers l''ouest. C''est un musée à ciel ouvert (centaines de statues), un terrain de sport (tennis, échecs, tai-chi) et le lieu de drague intellectuelle favori des étudiants de la Sorbonne voisine.', 'Odéon / Luxembourg (RER)', NULL, NULL);
