INSERT INTO places VALUES ('poi-043', 'Merci', 'merci-paris', 'café', NULL, 'chill', 48.8607214, 2.3668319, 3, '111 Bd Beaumarchais', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 15, 0, NULL, NULL, NULL, NULL, NULL, NULL, '{"chill":{"overall":24,"criteria":{}},"festif":{"overall":2,"criteria":{}},"culturel":{"overall":2,"criteria":{}}}', NULL, '["café"]', NULL, '{"primary_status":null,"tags":["terrasse"],"main_action":{"type":"site","url":"https://merci-merci.com/collections/toutes-les-nouveautes/?utm_source=GMB&utm_medium=organic","label":"Site du lieu"},"accessibility":true,"wifi_available":true,"opening_hours":"Tlj: 10h00 – 19h00","price_range":3,"smart_tip":"Leur gin tonic est servi dans de vrais grands verres ballon."}', '{"budget_avg":15,"is_free":false}', NULL, NULL, 'Merci est bien plus qu''un concept-store, c''est une destination. Situé dans un ancien atelier de tissus du Haut-Marais, on y accède par une cour pavée où trône toujours une petite Fiat 500 rouge (le spot photo incontournable). À l''intérieur, sous une verrière lumineuse, on découvre une sélection pointue de mode, de design et d''objets pour la maison, mise en scène comme dans un magazine. Mais Merci, c''est aussi un lieu de vie avec ses trois cafés, dont la fameuse Cantine Merci et le Café de la bibliothèque aux murs tapissés de 10 000 livres d''occasion.', 'Saint-Sébastien-Froissart', NULL, NULL);
INSERT INTO places_fts VALUES ('poi-043', 'Merci', 'café', NULL, '111 Bd Beaumarchais', '', 'Merci est bien plus qu''un concept-store, c''est une destination. Situé dans un ancien atelier de tissus du Haut-Marais, on y accède par une cour pavée où trône toujours une petite Fiat 500 rouge (le spot photo incontournable). À l''intérieur, sous une verrière lumineuse, on découvre une sélection pointue de mode, de design et d''objets pour la maison, mise en scène comme dans un magazine. Mais Merci, c''est aussi un lieu de vie avec ses trois cafés, dont la fameuse Cantine Merci et le Café de la bibliothèque aux murs tapissés de 10 000 livres d''occasion.');
INSERT INTO places VALUES ('poi-044', 'Dirty Dick', 'dirty-dick', 'bar', NULL, 'festif', 48.8815448, 2.3375062, 9, '10 Rue Frochot', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 16, 0, NULL, NULL, NULL, NULL, NULL, NULL, '{"chill":{"overall":2,"criteria":{}},"festif":{"overall":24,"criteria":{}},"culturel":{"overall":2,"criteria":{}}}', NULL, '["bar"]', NULL, '{"primary_status":null,"tags":["terrasse","no_happy_hour"],"main_action":{"type":"site","url":"https://www.facebook.com/Dirty-Dick-504787289561262/","label":"Site du lieu"},"accessibility":true,"wifi_available":true,"opening_hours":"Tlj: 19h00 – 2h00","price_range":3,"smart_tip":"Ambiance rock assurée, n y allez pas si vous cherchez le calme."}', '{"budget_avg":16,"is_free":false}', NULL, NULL, 'Ancien bar à hôtesses de Pigalle transformé en temple du Tiki, le Dirty Dick assume son héritage sulfureux avec humour. C''est une explosion de kitsch polynésien : fauteuils en rotin, chemises à fleurs, totems en bois et murs peints de jungle tropicale. Les cocktails sont servis dans des verres en forme de crâne, de coquillage ou de volcan fumant, et sont redoutablement chargés en rhum. C''est festif, bruyant, exotique et totalement dépaysant. L''endroit idéal pour faire la fête entre amis sans se prendre au sérieux.', 'Pigalle', NULL, NULL);
