INSERT INTO places VALUES ('poi-054', 'Déviant', 'deviant', 'bar', NULL, 'festif', 48.8737882, 2.3501233, 10, '39 R. des Petites Écuries', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 30, 0, NULL, NULL, NULL, NULL, NULL, NULL, '{"chill":{"overall":2,"criteria":{}},"festif":{"overall":24,"criteria":{}},"culturel":{"overall":2,"criteria":{}}}', NULL, '["bar"]', NULL, '{"primary_status":null,"tags":["terrasse","no_happy_hour"],"main_action":{"type":"site","url":"http://deviant.paris/","label":"Site du lieu"},"accessibility":true,"wifi_available":false,"opening_hours":"Mar-Sam: 19h-00h","price_range":3,"smart_tip":"L endroit parfait pour fêter une bonne nouvelle entre amis."}', '{"budget_avg":30,"is_free":false}', NULL, NULL, 'Le bar à manger le plus punk et le plus brillant du 10ème, petit frère du restaurant Vivant. Ici, pas de chaises, pas de résa, on mange debout accoudé au comptoir en marbre qui fait le tour de la cuisine ouverte. Les chefs envoient des petites assiettes explosives (ris de veau, couteaux à la plancha) à la minute, sous vos yeux. C''est bruyant, l''énergie est folle, le vin nature coule à flots. Une expérience culinaire ''in your face'' inoubliable.', 'À proximité', NULL, NULL);
