INSERT INTO places VALUES ('poi-057', 'Musée de l''Orangerie', 'muse-de-lorangerie', 'museum', NULL, 'culturel', 48.8637884, 2.3226724, 1, 'Jardin des Tuileries', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 15.5, 0, NULL, NULL, NULL, NULL, NULL, NULL, '{"chill":{"overall":2,"criteria":{}},"festif":{"overall":2,"criteria":{}},"culturel":{"overall":24,"criteria":{}}}', NULL, '["museum"]', NULL, '{"primary_status":null,"tags":["terrasse","payant"],"main_action":{"type":"site","url":"https://www.musee-orangerie.fr/fr","label":"Site du lieu"},"accessibility":true,"wifi_available":false,"opening_hours":"Tlj: 9h00 – 18h00","price_range":2,"smart_tip":"Leur tartelette citron est un équilibre parfait entre acidité et sucre."}', '{"budget_avg":15.5,"is_free":false}', NULL, NULL, 'Situé au bout du jardin des Tuileries, ce musée est l''écrin sur-mesure d''un chef-d''œuvre absolu : les Nymphéas de Monet. Les deux salles ovales, baignées de lumière naturelle zénithale, ont été conçues par le peintre lui-même pour immerger le spectateur dans ses paysages d''eau. C''est une expérience méditative unique au monde. Le musée abrite aussi la collection Walter-Guillaume (Renoir, Cézanne, Matisse, Picasso...), une concentration de chefs-d''œuvre impressionnistes et modernes dans un format à taille humaine.', 'À proximité', NULL, NULL);
