INSERT INTO places VALUES ('poi-068', 'Mokonuts', 'mokonuts', 'restaurant', NULL, 'chill', 48.8504, 2.3842, 11, '5 Rue Saint-Bernard', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 17.1, 0, NULL, NULL, NULL, NULL, NULL, NULL, '{"chill":{"overall":23.8,"criteria":{"noise_level":7,"seating_comfort":6,"natural_light":9,"crowd_density":5,"music_tempo":8}},"festif":{"overall":3,"criteria":{"event_programming":1,"dance_space":1,"cocktail_offering":2,"crowd_energy":6,"late_hours":2}},"culturel":{"overall":7.8,"criteria":{"architectural_originality":6,"artistic_presence":5,"cultural_programming":2,"creative_clientele":9,"workspace_facilities":1}}}', NULL, '["restaurant"]', NULL, '{"primary_status":null,"tags":[],"main_action":{"type":"site","url":"https://www.mokonuts.com/","label":"Site du lieu"},"accessibility":true,"wifi_available":false,"opening_hours":"Lun-Ven: 09h00 – 17h00","price_range":2,"smart_tip":"Les soirs de match, l ambiance est électrique mais bon enfant."}', '{"budget_avg":17.1,"is_free":false}', NULL, NULL, 'Mokonuts est l''une de ces pépites gastronomiques confidentielles qui font la légende du 11ème arrondissement. Ce ''pocket bistro'' est le fruit de l''amour et de la passion de Moko Hirayama, avocate américano-japonaise reconvertie en fée de la pâtisserie, et d''Omar Koreitem, chef né au Liban. Ensemble, ils ont créé un lieu qui refuse les étiquettes, à mi-chemin entre le coffee shop new-yorkais et la table d''auteur parisienne. La salle est minuscule (à peine 18 places), décorée avec une simplicité touchante qui donne l''impression d''être invité dans leur propre cuisine. On y vient le midi pour une cuisine du marché inventive, mêlant épices du Moyen-Orient et techniques japonaises, et toute la journée pour les cookies de Moko, célèbres dans tout Paris pour leur texture parfaite et leurs saveurs audacieuses (tahini, miso, chocolat noir). Mokonuts incarne cette nouvelle scène culinaire parisienne : cosmopolite, exigeante mais profondément généreuse.', 'Faidherbe-Chaligny', NULL, NULL);
