INSERT INTO places VALUES ('poi-088', 'Fréquence', 'frequence', 'bar', NULL, 'culturel', 48.85481499999999, 2.3759872, 11, '20 Rue Keller', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 15, 0, NULL, NULL, NULL, NULL, NULL, NULL, '{"chill":{"overall":6.4,"criteria":{"noise_level":4,"natural_light":6,"crowd_density":4}},"festif":{"overall":8,"criteria":{"crowd_energy":4,"late_hours":8}},"culturel":{"overall":25,"criteria":{"architectural_originality":10,"artistic_presence":4}}}', NULL, '["bar"]', NULL, '{"primary_status":null,"tags":["no_happy_hour"],"main_action":{"type":"site","url":"https://www.instagram.com/frequenceparis/","label":"Site du lieu"},"accessibility":true,"wifi_available":false,"opening_hours":"Tlj: Closed","price_range":1,"smart_tip":"Leurs vins du Rhône sont sélectionnés chez des petits vignerons."}', '{"budget_avg":15,"is_free":false}', NULL, NULL, 'À la fois bar à cocktails et salon d''écoute, Fréquence est le QG des amoureux du bon son et des mélanges précis. Ici, le mur de bouteilles laisse place à une collection massive de vinyles s''étendant du funk à la modern soul. Le décor brut (béton, bois, pierres apparentes) crée un cadre minimaliste apaisant. La carte des cocktails, aux influences japonaises marquées, propose des mélanges d''une finesse chirurgicale utilisant souvent du sake ou du shochu. Dès la nuit tombée, les platines s''animent pour transformer ce lieu intimiste en un dancefloor vibrant et authentique.', 'À proximité', NULL, NULL);
