INSERT INTO places VALUES ('poi-102', 'Dalia', 'dalia', 'restaurant', NULL, 'festif', 48.86786799999999, 2.3435539, 2, '93 Rue Montmartre', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 22, 0, NULL, NULL, NULL, NULL, NULL, NULL, '{"chill":{"overall":6.8,"criteria":{"noise_level":2,"natural_light":10,"crowd_density":2}},"festif":{"overall":25,"criteria":{"crowd_energy":10,"late_hours":8}},"culturel":{"overall":10,"criteria":{"architectural_originality":10,"artistic_presence":4}}}', NULL, '["restaurant"]', NULL, '{"primary_status":null,"tags":["gratuit_moins_26"],"main_action":{"type":"site","url":"https://www.daliagroup.com/dalia-paris","label":"Site du lieu"},"accessibility":true,"wifi_available":false,"opening_hours":"Tlj: 12:00 – 2:30 PM, 7:00 PM – 12:00 AM","price_range":3,"smart_tip":"Foncez sur la Giga-Chakchouka à partager. La déco terracotta est parfaite pour le feed Insta."}', '{"budget_avg":22,"is_free":false}', NULL, NULL, 'Dalia est l''une des tables les plus vibrantes du 2ème arrondissement. Ce restaurant dédié à la cuisine levantine propose une farandole d''assiettes à partager inspirées des saveurs d''Israël, du Liban et de Syrie. Sous la houlette du chef Or Bitan, les classiques (houmous, shakshuka, halloumi) sont revisités avec éclat et modernité. Le décor, aux tons terracotta et au mobilier en rotin, baigné par la lumière des grandes baies vitrées, transporte immédiatement les convives sur les rives du Levant. C''est bruyant, joyeux, généreux et idéal pour de grandes tablées festives où le partage est le seul maître-mot.', 'À proximité', NULL, NULL);
