INSERT INTO places VALUES ('poi-106', 'Clown Bar', 'clown-bar', 'restaurant', NULL, 'culturel', 48.8636036, 2.3672314, 11, '114 Rue Amelot', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 26, 0, NULL, NULL, NULL, NULL, NULL, NULL, '{"chill":{"overall":6.4,"criteria":{"noise_level":4,"natural_light":8,"crowd_density":4}},"festif":{"overall":8,"criteria":{"crowd_energy":4,"late_hours":8}},"culturel":{"overall":25,"criteria":{"architectural_originality":10,"artistic_presence":4}}}', NULL, '["restaurant"]', NULL, '{"primary_status":null,"tags":[],"main_action":{"type":"site","url":"https://www.clownbar.fr/","label":"Site du lieu"},"accessibility":true,"wifi_available":false,"opening_hours":"Tlj: 12:00 – 2:00 PM, 7:00 PM – 12:00 AM","price_range":3,"smart_tip":"Leurs vins d Alsace sont une vraie découverte pour les néophytes."}', '{"budget_avg":26,"is_free":false}', NULL, NULL, 'Ancienne cantine des artistes du Cirque d''Hiver voisin, le Clown Bar est un joyau Belle Époque miraculeusement préservé. Son plafond de céramique peinte, figurant des clowns et des acrobates, est classé Monument Historique. Mais derrière ce décor de musée se cache l''une des cuisines les plus radicales et brillantes de Paris. Les chefs y proposent des assiettes d''une créativité folle, n''hésitant pas à travailler des abats ou des accords terre-mer audacieux. C''est l''essence de la nouvelle garde culinaire : un respect immense pour l''histoire allié à une audace contemporaine totale. Une expérience hors du temps et des modes.', 'À proximité', NULL, NULL);
INSERT INTO places_fts VALUES ('poi-106', 'Clown Bar', 'restaurant', NULL, '114 Rue Amelot', '', 'Ancienne cantine des artistes du Cirque d''Hiver voisin, le Clown Bar est un joyau Belle Époque miraculeusement préservé. Son plafond de céramique peinte, figurant des clowns et des acrobates, est classé Monument Historique. Mais derrière ce décor de musée se cache l''une des cuisines les plus radicales et brillantes de Paris. Les chefs y proposent des assiettes d''une créativité folle, n''hésitant pas à travailler des abats ou des accords terre-mer audacieux. C''est l''essence de la nouvelle garde culinaire : un respect immense pour l''histoire allié à une audace contemporaine totale. Une expérience hors du temps et des modes.');
INSERT INTO places VALUES ('poi-107', 'Jones', 'jones', 'restaurant', NULL, 'festif', 48.856584, 2.380064, 11, '43 Rue Godefroy Cavaignac', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 22.7, 0, NULL, NULL, NULL, NULL, NULL, NULL, '{"chill":{"overall":6.4,"criteria":{"noise_level":4,"natural_light":8,"crowd_density":4}},"festif":{"overall":23.7,"criteria":{"crowd_energy":4,"late_hours":8}},"culturel":{"overall":8,"criteria":{"architectural_originality":8,"artistic_presence":4}}}', NULL, '["restaurant"]', NULL, '{"primary_status":null,"tags":[],"main_action":{"type":"site","url":"http://www.jonescaferestaurant.com/","label":"Site du lieu"},"accessibility":true,"wifi_available":false,"opening_hours":"Tlj: 7:00 – 10:30 PM","price_range":2,"smart_tip":"Leur playlist rock classique est parfaite pour une pinte au zinc."}', '{"budget_avg":22.7,"is_free":false}', NULL, NULL, 'Situé rue Godefroy Cavaignac, Jones est le digne héritier de la révolution bistronomique du 11ème. Dans un décor industriel aux lignes épurées et à la lumière douce, l''établissement cultive une approche sincère et décontractée de la gastronomie. La cuisine y est brute, mettant en valeur des produits de petits producteurs avec un minimalisme élégant. Mais c''est surtout la convivialité qui définit Jones : de grandes tables en bois, des rires généreux et une carte des vins naturels monumentale qui invite à la découverte. Une adresse de quartier pour initiés où l''on se sent immédiatement comme chez soi.', 'À proximité', NULL, NULL);
