INSERT INTO places VALUES ('poi-170', 'Parc Montsouris', 'parc-montsouris', 'parc', NULL, 'chill', 48.8219, 2.3378, 14, 'Rive Gauche, Paris', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, NULL, NULL, NULL, NULL, NULL, NULL, '{"chill":{"overall":23,"criteria":{}},"festif":{"overall":4,"criteria":{}},"culturel":{"overall":5,"criteria":{}}}', NULL, '["parc"]', NULL, '{"primary_status":null,"tags":["pelouse_autorisee"],"main_action":{"type":"site","url":"https://www.paris.fr/equipements/parc-montsouris-1810","label":"Site du lieu"},"accessibility":true,"wifi_available":false,"opening_hours":"Voir sur place","price_range":1,"smart_tip":"Pelouse autorisée toute l''année. Les arbres exotiques et le lac aux cygnes en font un vrai Central Park du 14e."}', '{"budget_avg":0,"is_free":true}', NULL, NULL, 'Havre de paix de 15 hectares au sud de Paris, le parc Montsouris est un chef-d''œuvre du jardin à l''anglaise du XIXe siècle. Son lac peuplé de cygnes, ses arbres centenaires exotiques et ses pelouses vallonnées invitent à la flânerie romantique. Sous les cascades artificielles ou sur les ponts en fer forgé, le temps s''arrête. C''est le spot préféré des étudiants de la Cité Universitaire voisine et des familles du 14ème pour un pique-nique bucolique ou un jogging matinal inspirant.', 'À proximité', NULL, NULL);
