INSERT INTO places VALUES ('poi-227', 'Dirty Lemon', 'dirty-lemon', 'bar', NULL, 'chill', 48.861, 2.373, 11, '24 Rue de la Folie Méricourt', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 25, 0, NULL, NULL, NULL, NULL, NULL, NULL, '{"chill":{"overall":15,"criteria":{}},"festif":{"overall":12,"criteria":{}},"culturel":{"overall":8,"criteria":{}}}', NULL, '["bar"]', NULL, '{"primary_status":null,"tags":["terrasse","no_happy_hour"],"main_action":null,"accessibility":true,"wifi_available":false,"opening_hours":"Lun-Sam: 18h-02h / Dim: 12h-17h (Brunch)","price_range":2,"smart_tip":"Leur brunch du dimanche est l''un des plus créatifs du quartier."}', '{"budget_avg":25,"is_free":false}', NULL, NULL, 'Dirty Lemon est un bar à cocktails vibrant et inclusif qui apporte une touche de fraîcheur et d''audace à la scène parisienne. Fondé par une équipe de femmes talentueuses, le lieu se définit par une énergie communicative, un décor acidulé et une carte de cocktails qui ne craint pas de bousculer les conventions. Ici, les saveurs sont vives, souvent citronnées ou herbacées, privilégiant les produits frais et les combinaisons inattendues. Que ce soit pour un apéro entre amis ou une soirée plus festive, Dirty Lemon s''est imposé comme un refuge chaleureux où la mixologie se veut accessible, créative et résolument moderne.', 'Saint-Ambroise', NULL, NULL);
